import React, { Component } from 'react'
import './App.css'
import NavBar from './Header'
import ProductsContainer from './ProductsContainer'
import SearchForm from './SearchForm'
import Header from './Header'
import Cart from './Cart'

class App extends Component {
  state = {
    products: [],
    searchTerm: '',
    itemsInCart: [],
    currentContainer: null,
  }

  componentDidMount() {
    fetch('http://localhost:3000/products')
      .then(response => response.json())
      .then(products => this.setState({ products }))
  }
  searchProducts = () => {
    let allProducts = this.state.products

    return allProducts.filter(product => {
      return product.name
        .toLowerCase()
        .includes(this.state.searchTerm.toLowerCase())
    })
  }

  increaseCartValue = () => {
    this.setState({ itemsInCart: this.state.itemsInCart.length })
  }

  addItemToCart = () => {
    this.setState({ itemsInCart: [...this.state.itemsInCart] })
  }

  getSearchTerm = e => {
    this.setState({
      searchTerm: e.target.value,
    })
  }

  showCart = () => {
    this.setState({ currentContainer: 1 })
  }

  render() {
    return (
      <div className="App">
        <NavBar itemsInCart={this.state.itemsInCart} showCart={this.showCart} />
        <SearchForm getSearchTerm={this.getSearchTerm} />
        {!this.state.currentContainer ? (
          <ProductsContainer
            products={this.searchProducts()}
            increaseCartValue={this.increaseCartValue}
          />
        ) : (
          <Cart />
        )}
      </div>
    )
  }
}

export default App
